mapper.select:
	select 
	  	count(1)
   	from 
   		SEARCH_SYNONYM_DICT_T T
	where
		1 = 1

			and  T.KEYWORD like #{synonymVo.keyword}  


			and  T.SYNONYM_WORD like #{synonymVo.synonymWord}  


			and  T.UNIDIR_WORD like #{synonymVo.unidirWord}

select * from( select TT.*, ROWNUM RN from (select T.ID, T.KEYWORD, T.SYNONYM_WORD, T.UNIDIR_WORD, T.DESCRIPTION, T.CREATER, T.CREATE_TIME, T.UPDATER, T.UPDATE_TIME from SEARCH_SYNONYM_DICT_T T where 1 = 1
and T.KEYWORD like #{synonymVo.keyword}
and T.SYNONYM_WORD like #{synonymVo.synonymWord}
and T.UNIDIR_WORD like #{synonymVo.unidirWord}
order by T.UPDATE_TIME desc) TT where ROWNUM <= #{page.endIndex} ) TTT where TTT.RN > #{page.startIndex}
